public class ConcatTwoStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String strOne = args[0];
		
		String strTwo = args[1];
		String bothStr = strOne+strTwo;
		System.out.println("Concatenated String '" + strOne + "' and '"+ strTwo+ "' to be "+ bothStr);
	}

}
